import React, { useState } from "react";
import ProgressBar from "@ramonak/react-progress-bar";
import "./Sidebar.css"
import '../../reset.css'
import './ProgressBar.scss'
import humburgerIcon from '../../imgs/icons8-circled-menu-100.png'
import Exercise from "../../Classes/Execirse";

interface Iprops {
    excersises: Exercise[]
}

export default function SideBar(props: Iprops) {
    const [curentEx, SetCurentEx] = useState(1);
    return (
        <div className="sidenav">
            <LevelCounter curentLevel={curentEx} exercisesLenght={props.excersises.length} />
            <ProgressBar completed={curentEx} maxCompleted={props.excersises.length}
                className="progress-bar"
                baseBgColor='#808080'
                height='5px'
                isLabelVisible={false}
                animateOnRender={true}
                width='365px'
                bgColor='rgb(34, 157, 73)'
            />
        </div>


    )
}

interface ILevelCounterProps {
    curentLevel: number,
    exercisesLenght: number
}

function LevelCounter(props: ILevelCounterProps) {
    return (

        <div className="level-bar">
            <div className="level-counter">
                <p className="counter">Задание {props.curentLevel} из {props.exercisesLenght}</p>
                <div className="checkmark">
                </div>
            </div>

            <div className="nav-level-bar">
                <div className="arrows">
                    <div className="arrow-div">
                        <div className="arrow_left" ></div>
                    </div>
                    <div className="arrow-div">
                        <div className="arrow_right" ></div>
                    </div>
                </div>

                <div >
                    <img className="hamburger-menu" src={humburgerIcon} alt="Задания" />
                </div>
            </div>
        </div>
    )
}
