import React, { useState } from "react";
import ProgressBar from "@ramonak/react-progress-bar";
import "./Sidebar.css"
import '../../reset.css'
import './ProgressBar.scss'
import humburgerIcon from '../../imgs/icons8-circled-menu-100.png'
import Exercise from "../../Classes/Execirse";

interface Iprops {
    excersises: Exercise[]
}

export default function SideBar(props: Iprops) {
    const [curentEx, SetCurentEx] = useState(1);


    
    function OnNextEx() {
        let curent = curentEx;
        SetCurentEx(++curent)
    }

    function OnPreviousEx() {
        let curent = curentEx;
        SetCurentEx(--curent)
    }

    return (
        <div className="sidenav">
            <LevelCounter 
            curentEx={props.excersises[curentEx]} 
            curentLevelIndex={curentEx} 
            exercisesLenght={props.excersises.length}
            OnNextEx={OnNextEx}
            OnPreviousEx={OnPreviousEx} />
            <ProgressBar completed={curentEx} maxCompleted={props.excersises.length}
                className="progress-bar"
                baseBgColor='#808080'
                height='5px'
                isLabelVisible={false}
                animateOnRender={false}
                width='365px'
                bgColor='rgb(34, 157, 73)'
            />
        </div>


    )
}

interface ILevelCounterProps {
    curentLevelIndex: number,
    exercisesLenght: number,
    curentEx: Exercise

    OnNextEx:() =>void
    OnPreviousEx:() =>void
}

function LevelCounter(props: ILevelCounterProps) {
    return (

        <div className="level-bar">
            <div className="level-counter">
                <p className="counter">Задание {props.curentLevelIndex} из {props.exercisesLenght}</p>
                <div className="checkmark">
                </div>
            </div>

            <div className="nav-level-bar">
                <div className="arrows">
                    <div className="arrow-div">
                        <div onClick={props.OnPreviousEx} className="arrow_left" ></div>
                    </div>
                    <div className="arrow-div">
                        <div onClick={props.OnNextEx} className="arrow_right" ></div>
                    </div>
                </div>

                <div >
                    <img className="hamburger-menu" src={humburgerIcon} alt="Задания" />
                </div>
            </div>
        </div>
    )
}
